package com.m3pro.groundflip.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.config.annotation.web.configurers.AbstractHttpConfigurer;import org.springframework.security.config.http.SessionCreationPolicy;import org.springframework.security.web.SecurityFilterChain;@Configuration@EnableWebSecuritypublic class SecurityConfig {	@Bean	protected SecurityFilterChain configure(HttpSecurity httpSecurity) throws Exception {		httpSecurity			.csrf(AbstractHttpConfigurer::disable)			.httpBasic(AbstractHttpConfigurer::disable)			.formLogin(AbstractHttpConfigurer::disable)			.sessionManagement((sessionManagement) ->				sessionManagement.sessionCreationPolicy(SessionCreationPolicy.STATELESS))			.authorizeHttpRequests((authorizeHttpRequests) ->				authorizeHttpRequests.anyRequest().permitAll());		return httpSecurity.build();	}}