package com.m3pro.groundflip.exception;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;import jakarta.servlet.http.HttpServletRequest;@RestControllerAdvicepublic class AppExceptionHandler {	@ExceptionHandler(AppException.class)	ResponseEntity<ErrorResponse> AppExceptionHandler(AppException appException, HttpServletRequest request) {		return ResponseEntity			.status(appException.getErrorCode().getHttpStatus())			.body(ErrorResponse.of(appException.getErrorCode()));	}}